-- ----------------------------
-- Table structure for atomtable
-- ----------------------------
DROP TABLE IF EXISTS `atomtable`;
CREATE TABLE `atomtable` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `元素名称` varchar(255) NOT NULL,
  `元素符号` varchar(255) NOT NULL,
  `英文名称` varchar(255) NOT NULL,
  `原子序数` int(11) NOT NULL,
  `相对原子质量` float NOT NULL,
  `物理性质` text,
  `重点知识` text,
  `原子介绍` text,
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of atomtable
-- ----------------------------
INSERT INTO `atomtable` VALUES ('1', '氢', 'H', 'Hydrog', '1', '1.008', '状态： 无味、无色、无臭、极易燃烧的气体。', '氧化物\r\nH2O（氧化氢）：生活中常见的水的化学式，H为+1价，O为-2价。\r\nH2O2（过氧化氢）：双氧水的主要成份，是常见的消毒水之一。H为+1价，O为-1价。\n氮化物\r\nNH3（氨）：氨气的化学式；气体极易溶于水反应，生成氨水。\r\n氯化物\r\nHCl（氯化氢）：分子是由一个氯原子和一个氢原子组成的；无色而有刺激性气味的气体。氯化氢水溶液为盐酸，是无色或微黄色的液体。\r\n硫化物\r\nH2S（硫化氢）：又称氢硫酸，常温时硫化氢是一种无色有臭鸡蛋气味的剧毒气体，应在通风处进行使用必须采取防护措施。\r\n碳化物\r\nCH4（碳化氢）：又称甲烷，在自然界分布很广，是天然气、沼气、油田气及煤矿坑道气的主要成分。它可用作燃料及制造氢气、碳黑、一氧化碳、乙炔、氢氰酸及甲醛等物质的原料。', '氢是元素周期表中的第一号元素，元素名来源于希腊文，原意是“水素”。氢是由英国化学家卡文迪许在1766年发现，称之为可燃空气，并证明它在空气中燃烧生成水。1787年法国化学家拉瓦锡证明氢是一种单质并命名。氢在地壳中的丰度很高，按原子组成占15.4%，但重量仅占1%。在宇宙中，氢是最丰富的元素。在地球上氢主要以化和态存在于水和有机物中。有三种同位素：氕、氘、氚。 \r\n　　氢在通常条件下为无色、无味的气体；气体分子由双原子组成；熔点-259.14°C，沸点-252.8°C，临界温度33.19K，临界压力12.98大气压，气体密度0.0899克/升；水溶解度21.4厘米³/千克水（0°C），稍溶于有机溶剂。\r\n　　在常温下，氢比较不活泼，但可用合适的催化剂使之活化。在高温下，氢是高度活泼的。除稀有气体元素外，几乎所有的元素都能与氢生成化合物。非金属元素的氢化物通常称为某化氢，如卤化氢、硫化氢等；金属元素的氢化物称为金属氢化物，如氢化锂、氢化钙等。\r\n　　氢是重要的工业原料，又是未来的能源。');
INSERT INTO `atomtable` VALUES ('2', '氦', 'He', 'Helium', '2', '4.003', '氦有两种天然同位素：氦3、氦4，自然界中存在的氦基本上是氦4。相对原子质量为4.003。1868年有人利用分光镜观察太阳表面，发现一条新的黄色谱线，并认为是属于太阳上的某个未知元素，故名氦。氦在空气中的含量为0.0005%。\r\n氦在通常情况下为无色、无味的气体；熔点-272.2℃(25个大气压)，沸点-268.9℃；密度0.1785克/升，临界温度-267.8℃，临界压力2.26大气压；水中溶解度8.61厘米³/千克水。\r\n氦是惰性元素之一，分子式为He，是一种稀有气体，无色、无臭、无味。它在水中的溶解度是已知气体中最小的，也是除氢气以外密度最小的气体。密度0.17847克/升，熔点-272.2℃（25个大气压）。沸点-268.9℃。它是最难液化的一种气体，其临界温度为-267.9℃。临界压力为2.25大气压。当液化后温度降到-270.98℃以下时，具有表面张力很小，导热性很强，几乎不呈现任何粘滞性。液体氦可以用来得到接近绝对零度（-273.15℃）的低温。化学性质十分不活泼，既不能燃烧，也不能助燃。氦也是最难液化的气体。\r\n氦在通常情况下为无色、无味的气体。是唯一不能在标准大气压下固化的物质。液态氦在温度下降至2.18K时，性质发生突变，成为一种超流体，能沿容器壁向上流动，热传导性为铜的800倍，并变成超导体；其比热容、表面张力、压缩性都是反常的。由于液氦的超低温，在此温度下出现了许多奇妙的物理现象。许多重要的物理实验，都要在低温下进行。世界各国的物理学家都在研究液态氦，希望通过液态氦达到更低的温度，研究各种物质在低温下会发生什么变化，会有什么我们还不知道的性质。这就产生了物理学的一个新的分支——低温物理学。', '已知的氦同位素有八种，包括氦3、氦4、氦5、氦6、氦8等，但只有氦3和氦4是稳定的，其余的均带有放射性。在自然界中，氦同位素中以氦4占最多，多是从其他放射性物质的α衰变，放出氦4原子核而来。而在地球上，氦3的含量极少，它们均是由超重氢（氚）的β衰变所产生。\r\n氦-2:它的原子核只有2个质子，到目前为止只是假想粒子，但如果强核力增强2%，它就有可能存在。\r\n氦-5，是氦的同位素之一，元素符号为He。它的原子核由二颗质子和三颗中子所组成。并带有放射性，会放出中子，其半衰期为0.6 MeV。\r\n氦-6:原子核包含2个质子和4个中子，非常不稳定。\r\n氦-7:原子核包含2个质子和5个中子，会衰变成氦-6，非常不稳定。\r\n氦-8:原子核包含2个质子和6个中子，非常不稳定。\r\n氦-9:原子核包含2个质子和7个中子，非常不稳定。\r\n氦-10:原子核包含2个质子和8个中子，非常不稳定。', '氦（Helium），为稀有气体的一种。元素名来源于希腊文，原意是“太阳” [1]  。1868年法国的杨森利用分光镜观察太阳表面，发现一条新的黄色谱线，并认为是属于太阳上的某个未知元素，故名氦。氦在通常情况下为无色、无味的气体，是唯一不能在标准大气压下固化的物质。氦是最不活泼的元素。氦的应用主要是作为保护气体、气冷式核反应堆的工作流体和超低温冷冻剂。');
INSERT INTO `atomtable` VALUES ('3', '锂', 'Li', 'Lithium', '3', '6.941', '银白色金属。质较软，可用刀切割。是最轻的金属，密度比所有的油和液态烃都小，故应存放于液体石蜡、固体石蜡或者白凡士林中(在液体石蜡中锂也会浮起)。\r\n锂的密度非常小，仅有0.534g/cm³，为非气态单质中最小的一个。\r\n因为锂原子半径小，故其比起其他的碱金属，压缩性最小，硬度最大，熔点最高。\r\n温度高于-117℃时，金属锂是典型的体心立方结构，但当温度降至-201℃时，开始转变为面心立方结构，温度越低，转变程度越大，但是转变不完全。在20℃时，锂的晶格常数为3.50Å，电导约为银的五分之一。*锂容易地与铁以外的任意一种金属熔合。\r\n锂的焰色反应为紫红色。', '锂的一些反应的化学反应方程式叙述如下：\r\n4 Li + O₂ = 2 Li₂O （反应条件：自发反应，或者加热，或者点燃）(燃烧猛烈） [3]  \r\n6 Li + N₂ = 2 Li₃N（反应条件：自发反应，或者加热，或者点燃）\r\n2 Li + S = Li₂S （该反应放出大量热，爆炸！）\r\n2 Li + 2 H₂O = 2 LiOH + H₂↑（现象：锂浮动在水面上，迅速反应，放出氢气）\r\n2 Li + 2 CH₃CH₂OH(乙醇) = 2 CH₃CH₂OLi(乙醇锂) + H₂↑\r\n4 Li + TiCl₄ = Ti + 4 LiCl\r\n2 Li + 2 NH₃(l.) = 2 LiNH₂ + H₂↑', '锂（Lithium），是一种化学元素，是金属活动性较强的金属（金属性最强的金属是铯），它的化学符号是Li，它的原子序数是3，三个电子其中两个分布在K层，另一个在L层。锂是所有金属中最轻的。因为锂的电荷密度很大并且有稳定的氦型双电子层，使得锂容易极化其他的分子或离子，自己本身却不容易极化。这一点就影响到它和它的化合物的稳定性。虽然锂的氢标电势是最负的，已经达到-3.045，但由于氢氧化锂溶解度不大而且锂与水反应时放热不能使锂融化，所以锂与水反应还不如钠剧烈，反应在进行一段时间后，锂表面的氮氧化物膜被溶解，从而使反应更加剧烈。在500℃左右容易与氢发生反应，产生氢化锂，是唯一能生成稳定得足以熔融而不分解的氢化物的碱金属，电离能5.392电子伏特，与氧、氮、硫等均能化合，是唯一的与氮在室温下反应，生成氮化锂（Li₃N）的碱金属。由于易受氧化而变暗。如果将锂丢进浓硫酸，那么它将在硫酸上快速浮动，燃烧并爆炸。如果将锂和氯酸钾混合（震荡或研磨），它也有可能发生爆炸式的反应。');
INSERT INTO `atomtable` VALUES ('4', '铍', 'Be', 'Beryllium', '4', '9.012', '铍是钢灰色金属轻金属。铍的硬度比同族金属高，不像钙、锶、钡可以用刀子切割。\r\n同位素\r\n铍 (原子质量单位: 9.012182(3) u )共有12个同位素，其中有1个是稳定的。', '铍和锂一样，在空气中形成保护性氧化层，故在空气中即使红热时也很稳定。不溶于冷水，微溶于热水，可溶于稀盐酸，稀硫酸和氢氧化钾溶液而放出氢。金属铍对于无氧的金属钠即使在较高的温度下，也有明显的抗腐蚀性。铍价态为正2价，可以形成聚合物以及具有显著热稳定性的一类共价化合物。', '铍作为一种新兴材料日益被重视，铍是原子能、火箭、导弹、航空、宇宙航行以及冶金工业中不可缺少的宝贵材料。\r\n(1)在所有的金属中，铍透过X射线的能力最强，有金属玻璃之称，所以铍是制造X射线管小窗口不可取代的材料。\r\n(2)铍是原子能工业之宝。在原子反应堆里，铍是能够提供大量中子炮弹的中子源（每秒钟内能产生几十万个中子）；铍对快中子有很强的减速作用，可以使裂变反应连续不断地进行下去，所以铍是原子反应堆中最好的中子减速剂。为了防止中子跑出反应堆危及工作人员的安全，反应堆的四周得有一圈中子反射层，用来强迫那些企图跑出反应堆的中子返回反应堆中去。铍的氧化物不仅能够像镜子反射光线那样把中子反射回去，而且熔点高，特别能耐高温，是反应堆里中子反射层的最好材料。\r\n(3)铍是优秀的宇航材料。人造卫星的重量每增加一公斤，运载火箭的总重量就要增加大约500kg。制造火箭和卫星的结构材料要求重量轻、强度大。铍比常用的铝和钛都轻，强度是钢的四倍。铍的吸热能力强，机械性能稳定。');
INSERT INTO `atomtable` VALUES ('5', '硼', 'B', 'Boron', '5', '10.81', '单质硼为黑色或深棕色粉末，在空气中氧化时由于三氧化二硼膜的形成而阻碍内部硼继续氧化。常温时能与氟反应，不受盐酸和氢氟酸水溶液的腐蚀。硼不溶于水，粉末状的硼能溶于沸硝酸和硫酸，以及大多数熔融的金属如\r\n铜、铁、锰、铝和钙。熔点2076℃。沸点3927℃。硼在常温时为弱电导体，而在高温时导电良好。单质硼有多种同素异形体，无定形硼为棕色粉末，晶体硼呈灰黑色。单质硼的硬度近似于金刚石，有很高的电阻，但它的导电率却随着温度的升高而增大。晶态硼较惰性，无定形硼则比较活泼。硼共有14种同位素，其中只有两个是稳定的。\r\n室温时为弱导电体；高温时则为良导体。在自然界中主要以硼酸和硼酸盐的形式存在。', '高温下B能与N₂、O₂、S、X₂等单质反应，例如它能在空气中燃烧生成B₂O₃和少量BN，在室温下即能与F₂发生反应，但它不与H₂、稀有气体等作用。\r\n(2)B能从许多稳定的氧化物（如SiO₂，P₂O₅，H₂O等）中夺取氧而用作还原剂。例如在赤热下，B与水蒸气作用生成硼酸和氢气：2B+6H₂O=高温=2H₃BO₃+3H₂\r\n(3)与酸作用\r\n硼不与盐酸作用，但与热浓H₂SO₄，热浓HNO₃作用生成硼酸：\r\n2B+3H₂SO₄(浓)==2H₃BO₃+3SO₂↑\r\nB+3HNO₃(浓)==H₃BO₃+ 3NO₂↑\r\n(4)与强碱作用\r\n在氧化剂存在下，硼和强碱共熔得到偏硼酸盐：\r\n2B+2NaOH+3KNO₃==2NaBO₂+3KNO₂+H₂O\r\n(5)与金属作用\r\n高温下硼几乎能与所有的金属反应生成金属硼化物。它们是一些非整比化合物，组成中B原子数目越多，其结构越复杂。', '硼元素是核糖核酸形成的必需品，而核糖核酸是生命的重要基础构件。夏威夷大学宇航局天体生物学研究所的博士后研究员詹姆斯-斯蒂芬森称：“硼对于地球上生命的起源可能很重要，因为它可以使核酸稳定，核酸是核糖核酸的重要成分。在早期生命中，核糖核酸被认为是脱氧核糖核酸的信息前体。”');
INSERT INTO `atomtable` VALUES ('6', '碳', 'C', 'Carbon', '6', '12.011', '现代已知的同位素共有十五种，有碳8至碳22，其中碳12和碳13属稳定型，其余的均带放射性，当中碳14的半衰期长达5730年，其他的为稳定同位素。 在地球的自然界里，碳12在所有碳的含量占98.93%，碳13则有1.07%。C的原子量取碳12、13两种同位素丰度加权的平均值，一般计算时取12.01。碳12是国际单位制中定义摩尔的尺度，以12克碳12中含有的原子数为1摩尔。碳14由于具有较长的半衰期，衰变方式为β衰变，碳14原子转变为氮原子 且碳是有机物的元素之一，生物在生存的时候，由于需要呼吸，其体内的碳14含量大致不变，生物死去后会停止呼吸，此时体内的碳14开始减少。人们可透过倾测一件古物的碳14含量，来估计它的大概年龄，这种方法称之为碳定年法。', '单质\r\n \r\n元素性质数据 \r\n在氧气中燃烧\r\n剧烈放热，发出刺眼白光，产生无色无味能使氢氧化钙溶液（澄清石灰水）变浑浊的气体\r\n　　化学方程式：\r\n　　C+O2==点燃==CO2（化合反应）\r\n在空气中燃烧\r\n　　放热，持续红热，产生无色无臭能使氢氧化钙溶液（澄清石灰水）变浑浊的气体CO2；当燃烧不充分，即氧气量不足时，产生一氧化碳：\r\n　　氧气充足时化学方程式：\r\n　　C+O2==点燃==CO2（化合反应）\r\n氧气不足时化学方程式：\r\n　　2C+O2==点燃==2CO（化合反应）\r\n作为还原剂\r\n　　碳作为还原剂拥有和氢气、一氧化碳相似的化学性质（但生成物不同），都可以从金属氧化物中还原出金属单质。\r\n　　碳还原氧化铜：\r\n　　C+2CuO==高温==2Cu+CO2↑（置换反应）\r\n　　碳还原氧化铁：\r\n　　3C+2Fe2O3==高温==4Fe+3CO2↑(置换反应)\r\n　　碳还原二氧化碳：\r\n　　C+CO2==高温==2CO（化合反应）\r\n　　但是，碳在密封空间与高锰酸钾共热，高锰酸钾会分解出氧气，碳会迅速氧化，会发生爆炸。\r\n与强氧化性酸反应：\r\nC+2H2SO4(浓）==加热==CO2↑+2SO2↑+2H2O\r\nC+4HNO3（浓）==加热==CO2↑4NO2↑+2H2O\r\n稳定性\r\n　　碳在“常温”下具有稳定性，不易反应，故古代名画现代能保存，书写档案要用碳素墨水 \r\n\r\n\r\n\r\n\r\n化合物\r\n碳的化合物中，只有以下化合物属于无机物：碳的氧化物、碳化物、碳的硫属化合物、二硫化碳(CS2)、碳酸盐、碳酸氢盐、氰及一系列拟卤素及其拟卤化物、拟卤酸盐，如氰[(CN)2]、氧氰[(OCN)2]，硫氰[(SCN)2]，其它含碳化合物都是有机化合物。\r\n由于碳原子形成的键都比较稳定，有机化合物中碳的个数、排列以及取代基的种类、位置都具有高度的随意性，因此造成了有机物数量极其繁多这一现象，现代人类发现的化合物中有机物占绝大多数。有机物的性质与无机物大不相同，它们一般可燃、不易溶于水，反应机理复杂，已形成一门独立的分科——有机化学。', '碳是一种非金属元素，位于元素周期表的第二周期IVA族。拉丁语为Carbonium，意为“煤，木炭”。碳是一种很常见的元素，它以多种形式广泛存在于大气和地壳和生物之中。碳单质很早就被人认识和利用，碳的一系列化合物——有机物更是生命的根本。碳是生铁、熟铁和钢的成分之一。 碳能在化学上自我结合而形成大量化合物，在生物上和商业上是重要的分子。生物体内绝大多数分子都含有碳元素。');
INSERT INTO `atomtable` VALUES ('7', '氮', 'N', 'Nitrogen ', '7', '14.006', '氮气为无色、无味的气体，氮本身无毒，熔点-209.86°C，沸点-195.8°C，气体密度1.25046克/升，临界温度-146.95°C，临界压力33.54大气压。', '化学键能: (kJ /mol)\r\nN-H 390\r\nN-N 160\r\nN=N 415\r\nN≡N(氮气) 948\r\nN-Cl 193\r\nN-C 286\r\nN=C 615\r\nN≡C 887', '相对原子量为14.006747。元素名来源于希腊文，原意是\"硝石\"。1772年由瑞典药剂师舍勒和英国化学家卢瑟福同时发现，后由法国科学家拉瓦锡确定是一种元素。氮在地壳中的含量为0.0046%，自然界绝大部分的氮是以单质分子氮气的形式存在于大气中，氮气占空气体积的78%。氮的最重要的矿物是硝酸盐。氮有两种天然同位素:氮14和氮15，其中氮14的丰度为99.625%。');
INSERT INTO `atomtable` VALUES ('8', '氧', 'O', 'Oxygen ', '8', '15.9994', '氧的同位素已知的有十七种，包括氧-12至氧-28，其中氧-16、氧-17和氧-18三种属于稳定型，其他已知的同位素都带有放射性，其半衰期全部均少于三分钟。利用医用回旋加速器产生的质子，轰击重氧水之中的氧-18，通过(p,n)核反应，从而获得可以发射正电子的放射性同位素氟-18离子，用于合成正电子发射计算机断层扫描检查所需的示踪剂氟代脱氧葡萄糖。', '氧的非金属性和电负性仅次于氟，除了氦、氖、氩、氪，所有元素都\r\n能与氧起反应。一般而言，绝大多数非金属氧化物的水溶液呈酸性，而碱金属或碱土金属氧化物则为碱性。此外，大多数有机化合物，可在氧气中燃烧生成二氧化碳与水蒸气，如酒精，甲烷。部分有机物不可燃，但也能和氧气等氧化剂发生氧化反应。', '氧(Oxygen)是一种化学元素，其原子序数为8，相对原子质量为15.9994。由符号\"O\"表示。在元素周期表中，氧是氧族元素的一员，它也是一个高反应性的第2周期非金属元素，很容易与几乎所有其它元素形成化合物(主要为氧化物)。在标准状况下，两个氧原子结合形成氧气，是一种无色无臭无味的双原子气体，化学式为O2。如果按质量计算，氧在宇宙中的含量仅次于氢和氦，在地壳中，氧则是含量最丰富的元素。氧不仅占了水质量的89%，也占了空气体积的20.9%。 ');

